package com.quan.example;

public class AntoInc {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		System.out.println("i : " + i);
		System.out.println("++i : " + ++i);
		System.out.println("i++ : " + i++);
		System.out.println("i : " + i);
		System.out.println("--i : " + --i);
		System.out.println("i-- : " + i--);
		System.out.println("i : " + i);
	}

}
